mapper.insert:
INSERT INTO
tb_group_user(group_name, member_seq, company_seq, menu_large, menu_medium, menu_function, member_gb) 
VALUES(#{groupName},#{memberSeq},#{companySeq},#{menuLarge},#{menuMedium},#{menuFunction},#{memberGb});

mapper.select:
select count(*) from ( SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq != 0 AND company_seq = 0
AND group_name like CONCAT('%', #{searchValue}, '%')
UNION SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq=0 AND company_seq=0 AND member_gb is not null
AND group_name like CONCAT('%', #{searchValue}, '%')
) userGroup ;

SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq != 0 AND company_seq = #{companySeq}
AND group_name like CONCAT('%', #{searchValue}, '%')
UNION SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq=0 AND company_seq=0 AND member_gb is null
AND group_name like CONCAT('%', #{searchValue}, '%')
ORDER BY groupSeq desc, groupName desc LIMIT #{startNo}, 10 ;

select count(*) from ( SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq != 0 AND company_seq = #{companySeq}
AND group_name like CONCAT('%', #{searchValue}, '%')
UNION SELECT group_seq as groupSeq, group_name as groupName, member_seq as memberSeq, company_seq as companySeq, menu_large as menuLarge, menu_medium as menuMedium, menu_function as menuFunction FROM tb_group_user WHERE member_seq=0 AND company_seq=0 AND member_gb is null
AND group_name like CONCAT('%', #{searchValue}, '%')
) userGroup ;

mapper.update:
UPDATE
	tb_group_user
SET
	group_name=#{groupName},
	menu_large=#{menuLarge},
	menu_medium=#{menuMedium},
	menu_function=#{menuFunction}
WHERE
	group_seq=#{groupSeq}
;
