mapper.select:
SELECT license.license_seq as licenseSeq, license.license_num as licenseNum, license.license_status as licenseStatus, license.generate_dt as generateDt, license.regist_dt as registDt, expire_dt as expireDt, license.user_copy_count as userCopyCount, member.user_id as userId, license.license_gb as licenseGb, period_gb as periodGb FROM tb_license as license LEFT OUTER JOIN tb_member as member ON license.license_user_seq = member.user_seq WHERE 1=1
AND license.license_status = #{searchStatus}
AND member.user_id like CONCAT('%', #{searchMember}, '%')
ORDER BY licenseSeq DESC LIMIT #{startNo}, 10 ;

SELECT count(*) FROM ( SELECT license.license_seq as licenseSeq, license.license_num as licenseNum, license.license_status as licenseStatus, license.generate_dt as generateDt, license.regist_dt as registDt, expire_dt as expireDt, license.user_copy_count as userCopyCount, member.user_id as userId, license.license_gb as licenseGb FROM tb_license as license LEFT OUTER JOIN tb_member as member ON license.license_user_seq = member.user_seq WHERE 1=1
AND license.license_status = #{searchStatus}
AND member.user_id like CONCAT('%', #{searchMember}, '%')
) license ;

mapper.insert:
INSERT INTO
tb_license(license_num, license_status, generate_dt, user_copy_count, period_gb) 
VALUES(#{licenseNum},#{licenseStatus},NOW(),#{userCopyCount},#{periodGb});

mapper.update:
UPDATE
	tb_license
SET
	license_status=4,
	disposal_reason=#{disposalReasonText}
WHERE
	license_seq=#{licenseSeq}
;

mapper.select:
SELECT count(*) FROM tb_license_sub as licenseUser INNER JOIN tb_member as member ON licenseUser.license_user_seq = member.user_seq WHERE licenseUser.license_seq = #{licenseSeq}
AND member.user_id like CONCAT('%', #{searchValue}, '%')
;

SELECT licenseUser.device_name as deviceName, licenseUser.device_os as deviceOs, licenseUser.use_start_dt as useStartDt, licenseUser.licensesub_seq as licensesubSeq, member.user_id as userId FROM tb_license_sub as licenseUser INNER JOIN tb_member as member ON licenseUser.license_user_seq = member.user_seq WHERE licenseUser.license_seq = #{licenseSeq}
AND member.user_id like CONCAT('%', #{searchValue}, '%')
ORDER BY licenseUser.use_start_dt DESC, licenseUser.licensesub_seq DESC LIMIT #{startNo}, 5 ;

SELECT 
	user_seq as userSeq, company_seq as companySeq, user_gb as userGb, user_status as userStatus
FROM
	tb_member
WHERE
	user_id = #{userId}
	and user_pw = #{userPwd}
;
